    setVideoId(data?.results[0]?.key);
  };
  useEffect(() => {
    fetchVideo();
  }, []);
  return (
    <div>
           <button onClick={handleClose}>close</button>
      <YouTube videoId={videoId} opts={opts} style={styles} closed={closed}/>;
    </div>
  );
};

export default ModalContent;
