import React, {useState} from "react";
import Modal from '@mui/material/Modal/index';
import ModalContent from './ModalContent';

const AppModal = ({children, id}) => {
    const [open, setOpen] = useState(false);
    const handleOpen = () => {
        setOpen(true);
    }
    const handleClose = () => {
        setOpen(false);
    }
    const modalStyles = {
        content: {
          top: "50%",
          left: "50%",
          right: "auto",
          bottom: "auto",
          marginRight: "-50%",
          transform: "translate(-50%, -50%)",
          maxWidth: "800px",
          width: "80%",
          minHeight: "480px",
        },
      };
    return (
        <div className="youtube-video-trailer"><div onClick = {handleOpen}> {children}</div>
        <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
        >
      <ModalContent id={id} style = {modalStyles}/>
        </Modal></div>
    )



}


export default AppModal;
