import React, { useState, useEffect } from "react";
import Genres from './Genres'

function Movie() {
    const [movieList, setMovieList] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(10);
    const [genres, setGenres] = useState([]);

    const getMovie = () => {  


        const discoverEndpoint = `https://api.themoviedb.org/3/discover/movie?api_key=${process.env.REACT_APP_MOVIE_KEY}&page=${currentPage}`;
        const searchEndpoint = `https://api.themoviedb.org/3/search/movie?api_key=${process.env.REACT_APP_MOVIE_KEY}}&query=${searchQuery}`;
        const genreEndpoint = `https://api.themoviedb.org/3/genre/movie/list?api_key=${process.env.REACT_APP_MOVIE_KEY}`;
        const endpoints = searchQuery ? searchEndpoint : discoverEndpoint;
        const options = {
       method: 'GET',
       headers: {
         accept: 'application/json',
         Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiIxMDk0MDk3MWZlYzJlNWZiNDlkM2QyZTQ3OTdkOWRhNSIsInN1YiI6IjY1NjU2MDE0MTU2Y2M3MDEwY2IzMTUxMSIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.S8rXI1em-12sf3qRFhWknWLPRSVxOJnGnyFjK6XsdLI'
       }
     };
     //?include_adult=false&include_video=false&language=en-US&page=1&sort_by=popularity.desc

         fetch(endpoints, options)
        .then(response => response.json())
        .then(response => setMovieList(response.results.slice(0, 60))
        // .then( setTotalPages(movieList.total_pages)

        )

        .catch(err => console.error(err))

    }

        useEffect(() => {
           getMovie();
       }, [searchQuery, currentPage, totalPages])

       console.log(genres);
       const showAll = () => {
        getMovie();
       }
       const prevPage = () => {
        if(currentPage > 1) {
            setCurrentPage((prev) => prev - 1)
        }
       }
       const nextPage = () => {
        if(currentPage < totalPages) {
            setCurrentPage((next) => next + 1)
        }
       }
        return (
            <div>
                <Genres genres = {genres} setGenres = {setGenres}/>
                 <div className="searchBar">
                  <input type="text" value={searchQuery}
                   onChange = {(e) => {
                    setSearchQuery(e.target.value)
                    console.log(e.target.value)
                   }}
                  />
                  <button onCLick={showAll}>Show All</button>
                </div>
                {
                    movieList.map((movie) => (
                        <>
                        <img src = {`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}/>
                        <p>{movie.original_title}</p>
                        <p>{movie.overview}</p>
                        <p>{movie.release_date}</p>
                        </>
                    ))
                }

                    {currentPage > 1 && (
                        <button  onClick = {prevPage} > Back </button>
                    )}

                <p>Page {currentPage}</p>
                {currentPage < totalPages && (
                      <button onClick = {
                        nextPage
                    } > Next </button>
                )}

            </div>
        )


}

export default Movie
