// export default ModalContent;
import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube";
import axios from "axios";

const ModalContent = ({ id, onClose }) => {
  const [videoId, setVideoId] = useState("");
  const closeButtonRef = useRef(null); // Ref for the close button
  const modalRef = useRef(null); // Ref for the modal container

  const opts = {
    height: "390",
    width: "840",
    playerVars: {
      autoplay: 1,
    },
  };

  const fetchVideo = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/movie/${id}/videos?api_key=${process.env.REACT_APP_MOVIE_KEY}`
    );
    setVideoId(data?.results[0]?.key);
  };

  useEffect(() => {
    fetchVideo();
  }, [id]);

  useEffect(() => {
    // Focus the close button when the modal opens
    if (closeButtonRef.current) {
      closeButtonRef.current.focus();
    }

    const handleKeyDown = (event) => {
      if (event.key === "Escape") {
        onClose();
      }

      // Handle Tab key to trap focus within the modal
      if (event.key === "Tab") {
        if (!modalRef.current) return; // Check if modalRef is available
        const focusableElements = modalRef.current.querySelectorAll(
          'button, [href], input, [tabindex]:not([tabindex="-1"])'
        );

        const firstElement = focusableElements[0];
        const lastElement = focusableElements[focusableElements.length - 1];

        // Cycle focus between iframe and close button
        if (event.shiftKey) {
          // Shift + Tab
          if (document.activeElement === firstElement) {
            event.preventDefault();
            lastElement.focus(); // Move focus to the last element (YouTube iframe)
          }
        } else {
          // Tab
          if (document.activeElement === lastElement) {
            event.preventDefault();
            closeButtonRef.current.focus(); // Move focus to the close button
          } else if (document.activeElement === closeButtonRef.current) {
            event.preventDefault();
            firstElement.focus(); // Move focus back to the YouTube iframe
          }
        }
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onClose]);

  const styles = {
    position: "fixed",
    right: "0",
    bottom: "0",
    top: "20%",
    left: "23%",
  };

  return (
    <div ref={modalRef} role="dialog" aria-labelledby="video-modal-title" aria-describedby="video-modal-description" style={styles}>
      <h2 id="video-modal-title">Watch Trailer</h2>
      <button ref={closeButtonRef} onClick={onClose} aria-label="Close video modal">
        Close
      </button>
      <YouTube 
        videoId={videoId}
        opts={opts}
        tabIndex="0" // Make the YouTube frame focusable
        style={styles}
      />
    </div>
  );
};

export default ModalContent;
