    const [open, setOpen] = useState(false);
    const handleOpen = () => {
        setOpen(true);
    }
    // const handleClose = () => {
    //     setOpen(false);
    // }

    return (
        <>
        <button >close</button>
        <div className="youtube-video-trailer">
        <div onClick = {handleOpen}> {children}</div>

        <Modal
        open={open}
        // onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
        >
      <ModalContent id={id} />

        </Modal></div>
        </>

    )



}


export default AppModal;
