import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube";
import axios from "axios";

const ModalContent = ({ id, onClose }) => {
  const [videoId, setVideoId] = useState("");
  // const [youTubeLink, setYouTubeLink] = useState("");
  const closeButtonRef = useRef(null); // Ref for the close button
  const modalRef = useRef(null); // Ref for the modal container

  const opts = {
    height: "390",
    width: "840",

    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };

  const fetchVideo = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/movie/${id}/videos?api_key=${process.env.REACT_APP_MOVIE_KEY}`
    );
    console.log(data);
    setVideoId(data?.results[0]?.key);
  };
  useEffect(() => {
    fetchVideo();
  }, []);
  useEffect(() => { // Effect to handle focus and keyboard navigation
    // Focus the close button when the modal opens
    if (closeButtonRef.current) { // Check if close button ref is set
      closeButtonRef.current.focus(); // Focus the close button
    }

    const handleKeyDown = (event) => { // Function to handle keydown events
      if (event.key === "Escape") { // Check if Escape key is pressed
        onClose(); // Call onClose function to close the modal
      }

      // Handle Tab key to trap focus between close button and YouTube play/pause button
      if (event.key === "Tab") { // Check if Tab key is pressed
        const playPauseButton = document.querySelector(".ytp-play-button"); // Select YouTube play/pause button

        if (event.shiftKey) { // Check if Shift key is also pressed
          // Shift + Tab
          if (document.activeElement === closeButtonRef.current) { // If focus is on the close button
            event.preventDefault(); // Prevent default behavior
            playPauseButton.focus(); // Focus on the play/pause button
          }
        } else { // If not holding Shift
          // Tab
          if (event.key === "Tab" && document.activeElement === playPauseButton) { // If focus is on the play/pause button
            event.preventDefault(); // Prevent default behavior
            closeButtonRef.focus(); // Focus on the close button
          }
        }
      }
    };

    window.addEventListener("keydown", handleKeyDown); // Attach keydown event listener to the window
    return () => { // Cleanup function
      window.removeEventListener("keydown", handleKeyDown); // Remove event listener on unmount
    };
  }, [onClose]); 
  return (
    <div>
        <button ref={closeButtonRef} onClick={onClose}  >Close</button> {/* Close Button */}
      <YouTube 
      videoId={videoId}
       opts={opts}
       // Attach the ref to the YouTube component
        tabIndex="0" // Make the YouTube frame focusable

     />;
    </div>
  );
};

export default ModalContent;
