import React, { useState, useEffect, useRef } from "react";
import YouTube from "react-youtube";
import axios from "axios";

const ModalContent = ({ id, onClose }) => {
  const [videoId, setVideoId] = useState("");
  const [youTubeLink, setYouTubeLink] = useState("");
  const closeButtonRef = useRef(null); // Ref for the close button
  const videoRef = useRef(null); // Ref for the YouTube video

  const opts = {
    height: "390",
    width: "840",

    playerVars: {
      // https://developers.google.com/youtube/player_parameters
      autoplay: 1,
    },
  };

  const styles = {
    position: "fixed",

    right: "0",
    bottom: "0",
    top: "20%",
    left: "23%",
  };

  const fetchVideo = async () => {
    const { data } = await axios.get(
      `https://api.themoviedb.org/3/movie/${id}/videos?api_key=${process.env.REACT_APP_MOVIE_KEY}`
    );
    console.log(data);
    setVideoId(data?.results[0]?.key);
  };
  useEffect(() => {
    fetchVideo();
  }, []);

  useEffect(() => {
    // Focus the YouTube video frame when the modal opens
    if (videoRef.current) {
      videoRef.current.focus(); // Move focus to the YouTube video
    }

    const handleKeyDown = (event) => {
      if (event.key === "Escape") {
        onClose();
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [onClose]);



  return (
    <div>
        <button ref={closeButtonRef} onClick={onClose}>Close</button> {/* Close Button */}
      <YouTube 
      videoId={videoId}
       opts={opts}
        ref={videoRef} // Attach the ref to the YouTube component
        tabIndex="0" // Make the YouTube frame focusable

      style={styles}  />;
    </div>
  );
};

export default ModalContent;
