      const options = {method: 'GET', headers: {accept: 'application/json'}};
      fetch(popularMovieEndpoint, options)
      .then(response => response.json())
      .then(response => console.log(response))
      .catch(err => setIsErr(true))
      useEffect(() => {
        getPopularMoviestoFeature();
