import { styled } from "@mui/material/styles";
import {Chip } from '@mui/material';

const MuiChipCustom = styled(Chip)(() => ({
    backgroundColor: '#000',
    borderRadius: 25,
    color: 'white',  
    margin: 14, 

    '& .MuiChip-label': {
       fontSize: 16
    },

    '& .MuiChip-deleteIcon': {
       color: 'blue',
       fontSize: 16
    },
    '&&:hover': {
      backgroundColor: 'lavender',
    }
 }));

 export default MuiChipCustom;
